Step.restore_redirect_calc:
if interview.instance.val() == 0:
	if interview.taxreturn.get().PRWS_DEATH_Y1.val()==1 and interview.taxreturn.get().PRWS_DEATH_N1.val()==0:
		if interview.taxreturn.get().PRWS_ACQYR1.val() > 1981:
			return "step|s_pr_t1255"
		else:
			return "step|s_pr_t1255_pre1982"
	else:
		if interview.taxreturn.get().PRWS_ACQYR1.val() > 1981:
			return "step|s_pr_t2091"
		else:
			return "step|s_pr_t2091_pre1982"
elif interview.instance.val() == 1:
	if interview.taxreturn.get().PRWS_DEATH_Y2.val()==1 and interview.taxreturn.get().PRWS_DEATH_N2.val()==0:
		if interview.taxreturn.get().PRWS_ACQYR2.val() > 1981:
			return "step|s_pr_t1255"
		else:
			return "step|s_pr_t1255_pre1982"
	else:
		if interview.taxreturn.get().PRWS_ACQYR2.val() > 1981:
			return "step|s_pr_t2091"
		else:
			return "step|s_pr_t2091_pre1982"
elif interview.instance.val() == 2:
	if interview.taxreturn.get().PRWS_DEATH_Y3.val()==1 and interview.taxreturn.get().PRWS_DEATH_N3.val()==0:
		if interview.taxreturn.get().PRWS_ACQYR3.val() > 1981:
			return "step|s_pr_t1255"
		else:
			return "step|s_pr_t1255_pre1982"
	else:
		if interview.taxreturn.get().PRWS_ACQYR3.val() > 1981:
			return "step|s_pr_t2091"
		else:
			return "step|s_pr_t2091_pre1982"
else:
	return "return|"

