Step.restore_redirect_calc:
if interviewState.curInterviewId.str() == 'i_pr_edit_property':
    if interview.instance.get() == 1:
        return "step|s_pr_entry_form_1"
    elif interview.instance.get() == 2:
        return "step|s_pr_entry_form_2"
    elif interview.instance.get() == 3:
        return "step|s_pr_entry_form_3"

if is_dual() and (interview.taxreturn.get() == sp):
    if tds.taxp_pr_instance.val() == 1:
        if taxp.PRWS_LINK1.val() == 100:
            sp.PRWS_SP_Y1.set(1)
            sp.PRWS_SP_N1.set(0)
            return "step|s_pr_entry_form_1"
        elif taxp.PRWS_LINK1.val() == 200:
            sp.PRWS_SP_Y2.set(1)
            sp.PRWS_SP_N2.set(0)
            return "step|s_pr_entry_form_2"
        elif taxp.PRWS_LINK1.val() == 300:
            sp.PRWS_SP_Y3.set(1)
            sp.PRWS_SP_N3.set(0)
            return "step|s_pr_entry_form_3"
        else:
            return "return|summary"
    elif tds.taxp_pr_instance.val() == 2:
        if taxp.PRWS_LINK2.val() == 100:
            sp.PRWS_SP_Y1.set(1)
            sp.PRWS_SP_N1.set(0)
            return "step|s_pr_entry_form_1"
        elif taxp.PRWS_LINK2.val() == 200:
            sp.PRWS_SP_Y2.set(1)
            sp.PRWS_SP_N2.set(0)
            return "step|s_pr_entry_form_2"
        elif taxp.PRWS_LINK2.val() == 300:
            sp.PRWS_SP_Y3.set(1)
            sp.PRWS_SP_N3.set(0)
            return "step|s_pr_entry_form_3"
        else:
            return "return|summary"
    elif tds.taxp_pr_instance.val() == 3:
        if taxp.PRWS_LINK3.val() == 100:
            sp.PRWS_SP_Y1.set(1)
            sp.PRWS_SP_N1.set(0)
            return "step|s_pr_entry_form_1"
        elif taxp.PRWS_LINK3.val() == 200:
            sp.PRWS_SP_Y2.set(1)
            sp.PRWS_SP_N2.set(0)
            return "step|s_pr_entry_form_2"
        elif taxp.PRWS_LINK3.val() == 300:
            sp.PRWS_SP_Y3.set(1)
            sp.PRWS_SP_N3.set(0)
            return "step|s_pr_entry_form_3"
        else:
            return "return|summary"

addr1 = ""
addr2 = ""
addr3 = ""

if interview.taxreturn.get().PRWS_APT1.str() != "":
    addr1 = addr1 + interview.taxreturn.get().PRWS_APT1.str()
if interview.taxreturn.get().PRWS_NUM1.str() != "":
    addr1 = addr1 + "-" + interview.taxreturn.get().PRWS_NUM1.str()
if interview.taxreturn.get().PRWS_ADDR1.str() != "":
    addr1 = addr1 + " " + interview.taxreturn.get().PRWS_ADDR1.str()
if interview.taxreturn.get().PRWS_LOT1.str() != "":
    addr1 = addr1 + " " + interview.taxreturn.get().PRWS_LOT1.str()

if interview.taxreturn.get().PRWS_APT2.str() != "":
    addr2 = addr2 + interview.taxreturn.get().PRWS_APT2.str()
if interview.taxreturn.get().PRWS_NUM2.str() != "":
    addr2 = addr2 + "-" + interview.taxreturn.get().PRWS_NUM2.str()
if interview.taxreturn.get().PRWS_ADDR2.str() != "":
    addr2 = addr2 + " " + interview.taxreturn.get().PRWS_ADDR2.str()
if interview.taxreturn.get().PRWS_LOT2.str() != "":
    addr2 = addr2 + " " + interview.taxreturn.get().PRWS_LOT2.str()

if interview.taxreturn.get().PRWS_APT3.str() != "":
    addr3 = addr3 + interview.taxreturn.get().PRWS_APT3.str()
if interview.taxreturn.get().PRWS_NUM3.str() != "":
    addr3 = addr3 + "-" + interview.taxreturn.get().PRWS_NUM3.str()
if interview.taxreturn.get().PRWS_ADDR3.str() != "":
    addr3 = addr3 + " " + interview.taxreturn.get().PRWS_ADDR3.str()
if interview.taxreturn.get().PRWS_LOT3.str() != "":
    addr3 = addr3 + " " + interview.taxreturn.get().PRWS_LOT3.str()

if (interviewState.curInterviewId.str()=='i_principal_residence') and ( (addr1 != "") or (addr2 != "") or (addr3 != "") ):
    return "return|summary"
elif (addr1 == ""):
    return "step|s_pr_entry_form_1"
elif (addr2 == ""):
    return "step|s_pr_entry_form_2"
elif (addr3 == ""):
    return "step|s_pr_entry_form_3"
else:
    return "return|summary"

